CREATE TABLE `works` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `title` VARCHAR(255) NOT NULL , `type` ENUM('IMG','VIDEO') NOT NULL DEFAULT 'IMG' , `source` VARCHAR(255) NOT NULL , `course` INT UNSIGNED NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;

ALTER TABLE `courses` ADD `page_description` VARCHAR(1000) NULL DEFAULT NULL AFTER `bonuses`, ADD `page_keywords` VARCHAR(1000) NULL DEFAULT NULL AFTER `page_description`;